import React, { useState } from "react";

export default function Textbox() {
    const [text,setText] = useState("")
    const onupper=()=>{
      alert('upper button is clicked')
      set
    }
    const onchange = (e)=>{
       setText(e.target.value)
    }
  return (
    <div className="container my-3">
      <div className="mb-3">
        <label for="exampleFormControlTextarea1" className="form-label">
          TextBox Apllication
        </label>
        <textarea className="form-control" id="exampleFormControlTextarea1" 
        rows="9" value={text} onChange={(e)=>{onchange(e)}}
        ></textarea>
      </div>
      <div className="container">
        <button onClick={onupper} type="button" class="btn btn-primary">Uppercase</button>
        <button type="button" class="btn btn-secondary">Lowercase</button>
        <button type="button" class="btn btn-success">Clear</button>
        <button type="button" class="btn btn-danger">Copy</button>
      </div>
    </div>
  );
}
